(function($){"use strict"; $('a.js-scroll-trigger[href*="#"]:not([href="#"])').click(function(){if (location.pathname.replace(/^\//, '')==this.pathname.replace(/^\//, '') && location.hostname==this.hostname){var target=$(this.hash); target=target.length ? target : $('[name=' + this.hash.slice(1) + ']'); if (target.length){$('html, body').animate({scrollTop: (target.offset().top - 48)}, 1000, "easeInOutExpo"); return false;}}}); $('.js-scroll-trigger').click(function(){$('.navbar-collapse').collapse('hide');}); $(window).scroll(function(){if ($("#mainNav").offset().top > 100){$("#mainNav").addClass("navbar-shrink"); $("#mainlogo").addClass("mainlogo-shrink"); $(".web-title").addClass('web-title-shrink');}else{$("#mainNav").removeClass("navbar-shrink"); $("#mainlogo").removeClass("mainlogo-shrink"); $(".web-title").addClass('web-title-shrink');}});})(jQuery);